//map HTML elements to variables
const ball = document.querySelector(".ball")

//random number function
function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
}

// sleep function

function sleep(milliseconds) {
    var start = new Date().getTime();
    for (var i = 0; i < 1e7; i++) {
        if ((new Date().getTime() - start) > milliseconds){
            break;
        }
    }
}


//beep function
function beep(times) {

    
    for (i = 0; i < times; i++) {
        const sound = new Audio("data:audio/wav;base64,");
        sound.play();
        sleep(1000)
        }
    
    }
    



//Set up a funciton for an onclick for the ball
ball.addEventListener('click', function () {

    //play a sound embedded base64 audio beep


    beep(5, 2000)
   


    //make 4  random numers in a var
    const rand0 = getRandomInt(450)
    const rand1 = getRandomInt(450)
    const rand2 = getRandomInt(450)
    const rand3 = getRandomInt(450)
    console.log("random number is", rand0, rand1, rand2, rand3)

    //set margins for the ball to move it to a random location 

    ball.style.marginLeft = rand0 + "px";
    ball.style.marginRight = rand1 + "px";
    ball.style.marginTop = rand2 + "px";
    ball.style.marginBottom = rand3 + "px";

    //function to change the text
    function changeText(text) {
        document.getElementById("ballstate").innerHTML = text
    }

    // make a check for the loction of the ball

    //middle box location check
    if (
        (rand0 > 200 && rand0 < 250)
        &&
        (rand2 > 200 && rand2 < 250)
    ) {
        changeText("Ball is in the middle");
        document.getElementById("ballstate").style.color = "green";

        for (let i = 0; i < 50; i++) {
            beep(10)
        }
    }
    // left box location check
    else if (rand0 < 200) {
        changeText("Ball is in the left");
        document.getElementById("ballstate").style.color = "firebrick";
    }
    //right box loction check
    else if (rand0 > 200) {
        changeText("Ball is in the right");
        document.getElementById("ballstate").style.color = "black";
    }

})